mapper.select:
SELECT au.id AS userId, au.user_name, au.nick_name, au.user_phone, au.user_email, au.user_status, au.user_sex, au.create_time, au.is_del, ar.role_name AS roleName, llt.last_login_time AS lastLoginTime, bd.base_name AS userSexShow, bd2.base_name AS userStatusShow FROM djmall_auth_user au LEFT JOIN djmall_auth_user_role aur ON au.id = aur.user_id LEFT JOIN djmall_auth_role ar ON aur.role_id = ar.id LEFT JOIN djmall_dict_base_data bd ON bd.base_code = au.user_sex LEFT JOIN djmall_dict_base_data bd2 ON bd2.base_code = au.user_status LEFT JOIN ( SELECT llt.user_id, MAX(llt.last_login_time) AS last_login_time FROM djmall_last_login_time llt GROUP BY llt.user_id ) AS llt ON au.id = llt.user_id
(au.user_name LIKE "%"#{userNameLike}"%" OR au.nick_name LIKE "%"#{userNameLike}"%" OR au.user_phone LIKE "%"#{userNameLike}"%" OR au.user_email LIKE "%"#{userNameLike}"%")
AND ar.id IN
#{userRole}
AND au.user_sex IN
#{userSex}
AND au.user_status = #{user.userStatus}
AND au.is_del = #{user.isDel}
ORDER BY au.id DESC

mapper.update:
UPDATE
djmall_auth_user
SET
is_del = #{isDel}
WHERE id IN

    #{id}
