mapper.select:
SELECT oi.order_no, oi.parent_order_no, oi.total_pay_money, oi.total_freight, oi.total_buy_count, oi.pay_type, oi.order_status, oi.create_time, oi.pay_time, oi.remind_status, oi.remind_time, GROUP_CONCAT( od.product_name, ':', od.sku_info ) productNameShow, od.sku_rate AS skuRateShow, bd.base_name AS payTypeShow, bd2.base_name AS orderStatusShow, ( SELECT (COUNT(0) - SUM(od.is_comment)) FROM djmall_order_detail od WHERE od.child_order_no = oi.order_no ) AS isAllComment FROM djmall_order_info oi LEFT JOIN djmall_order_detail od ON oi.order_no = od.child_order_no LEFT JOIN djmall_dict_base_data bd ON oi.pay_type = bd.base_code LEFT JOIN djmall_dict_base_data bd2 ON oi.order_status = bd2.base_code WHERE /*待发货 ： 已支付 已发货*/ oi.order_status IN ('PAID','SHIPPED')
/*已完成： 已完成 确认收货*/ oi.order_status IN ('COMPLETED','CONFIRM_RECEIPT')
/*已取消 ： 已取消*/ oi.order_status = #{orderStatus}
AND oi.buyer_id = #{userId} GROUP BY oi.order_no

SELECT oi.order_no, oi.parent_order_no, oi.total_money, oi.total_pay_money, oi.total_freight, oi.total_buy_count, oi.pay_type, oi.receiver_province, oi.receiver_city, oi.receiver_county, oi.receiver_name, oi.receiver_phone, oi.receiver_detail, oi.order_status, oi.create_time, oi.pay_time, oi.update_time, oi.remind_status, oi.remind_time, od.product_name AS productName, GROUP_CONCAT(od.sku_rate) AS skuRateShow, bd.base_name AS payTypeShow, bd2.base_name AS orderStatusShow, au.nick_name AS nickNameShow, au.user_phone AS userPhoneShow, au2.nick_name AS businessNickNameShow, GROUP_CONCAT(od.product_name) AS productNameShow FROM djmall_order_info oi LEFT JOIN djmall_order_detail od ON oi.order_no = od.child_order_no LEFT JOIN djmall_dict_base_data bd ON oi.pay_type = bd.base_code LEFT JOIN djmall_dict_base_data bd2 ON oi.order_status = bd2.base_code LEFT JOIN djmall_auth_user au ON oi.buyer_id = au.id LEFT JOIN djmall_auth_user au2 ON oi.business_id = au2.id
WHERE od.business_id = #{order.userId}
GROUP BY oi.order_no
