mapper.select:
SELECT ps.id AS productId, ps.product_name, ps.freight_id, ps.product_describe, ps.product_img, ps.product_type, ps.user_id, ps.product_status, ps.order_number, ps.praise_number, pf.freight AS freightShow, bd.base_name AS company, bd2.base_name AS productStatus FROM djmall_product_spu ps LEFT JOIN djmall_dict_product_freight pf ON ps.freight_id = pf.id LEFT JOIN djmall_dict_base_data bd ON pf.company = bd.base_code LEFT JOIN djmall_dict_base_data bd2 ON ps.product_status = bd2.base_code
ps.user_id = #{product.userId}
AND ps.product_name LIKE "%"#{productNameLike}"%"
AND ps.product_type IN
#{productType}
GROUP BY ps.id DESC

SELECT ps.id AS productId, ps.product_name, ps.product_describe, ps.product_img, ps.product_type, ps.product_status, ps.praise_number, pf.freight AS freightShow, bd.base_name AS company, psk.sku_count AS skuCountShow, psk.sku_price AS skuPriceShow, psk.sku_rate AS skuRateShow FROM djmall_product_spu ps LEFT JOIN djmall_dict_product_freight pf ON ps.freight_id = pf.id LEFT JOIN djmall_dict_base_data bd ON pf.company = bd.base_code LEFT JOIN djmall_product_sku psk ON psk.product_id = ps.id
psk.is_default = #{product.isDefault} AND ps.product_status = #{product.productStatus}
AND ps.product_name LIKE "%"#{productNameLike}"%"
AND psk.sku_price >= #{product.skuPriceMin}
AND psk.sku_price <= #{product.skuPriceMax}
AND ps.product_type IN
#{productType}
GROUP BY ps.id DESC
